import React, { useState, useEffect } from "react";

function index() {
  const [scrolled, setScrolled] = useState(false);

  useEffect(() => {
    window.addEventListener("scroll", () => {
      const newsLater = window.scrollY < window.innerHeight / 3;
      console.log(newsLater);
      if (newsLater !== true) {
        setScrolled(true);
      }
    });
  });
  return (
    <div>
      <div>
        <div
          className={
            scrolled
              ? "fixed bottom-0 left-0 bg-blue w-460 opacity-75"
              : "hidden"
          }
        >
          <div className="w-full">
            <p>x</p>
          </div>
        </div>
      </div>
    </div>
  );
}

export default index;
