import React, { useState, useEffect } from "react";

function index() {
  const [scrolled, setScrolled] = useState(false);

  useEffect(() => {
    window.addEventListener("scroll", () => {
      const newsLater = window.scrollY < window.innerHeight / 3;
      console.log(newsLater);
      if (newsLater !== true) {
        setScrolled(true);
      }
    });
  });
  return (
    <div>
      <div>
        <div
          className={
            scrolled
              ? "fixed bottom-0 left-0 bg-blue w-full lg:w-460 opacity-75"
              : "hidden"
          }
        >
          <div className="w-full py-2 px-4">
            <p className="text-right  text-white">x</p>
            <p className="text-white  font-bold text-2xl">
              Get Latest updates in web technologies
            </p>
            <p className="text-white">
              Lorem Ipsum is simply dummy text of the printing and typesetting
              industry. Lorem Ipsum has been the industry's standard dummy text
              ever since the 1500s
            </p>
            <form className="">
              <input
                className="w-full lg:w-3/5 px-4 my-4 h-8"
                placeholder="Email Address"
              />
              <button className="bg-darkorange opacity-100 lg:mx-4 w-full lg:w-40 h-8 text-white">
                Count me in !
              </button>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}

export default index;
